import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';
import logo from './logo.jpg';
import electronicsIcon from './electronics.png';
import appliancesIcon from './appliances.png';
import mechanicsIcon from './mech.png';
import { jwtDecode } from 'jwt-decode';

function App() {
  const [page, setPage] = useState('home');
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [isModalOpen, setModalOpen] = useState(false);
  const [posts, setPosts] = useState({});
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [currentImageIndex, setCurrentImageIndex] = useState({});
  const [comments, setComments] = useState({});
  const [users, setUsers] = useState([]);
  const [loggedInUser, setLoggedInUser] = useState(null);
  const [replies, setReplies] = useState({});
  const [profileImage, setProfileImage] = useState(null);
  const [profile, setProfile] = useState({
    firstName: '',
    lastName: '',
    zipCode: '',
    email: '',
    aboutMe: '',
  });

  const adminCredentials = { email: 'admin@example.com', password: 'adminpass' };

  // Navigation handler
  const handleNavigate = (targetPage) => {
    if (targetPage === 'admin' && (!loggedInUser || !loggedInUser.isAdmin)) {
      setPage('adminLogin');
    } else {
      setPage(targetPage);
    }
  };

  // Fetch posts and their comments
  const fetchPosts = async (category) => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(`http://localhost:5000/api/posts/${category}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setPosts((prev) => ({ ...prev, [category]: response.data }));
      fetchCommentsForPosts(response.data);
    } catch (error) {
      console.error('Error fetching posts:', error.message);
    }
  };

  const fetchCommentsForPosts = async (categoryPosts) => {
    const token = localStorage.getItem('token');
    try {
      const allComments = {};
      for (const post of categoryPosts) {
        const response = await axios.get(
          `http://localhost:5000/api/comments/${post.id}`,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        allComments[post.id] = response.data;
      }
      setComments(allComments);
    } catch (error) {
      console.error('Error fetching comments:', error.message);
    }
  };

  // Handle adding a comment
  const handleAddComment = async (postId, category, commentText) => {
    if (!commentText.trim()) return alert('Comment cannot be empty.');
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        'http://localhost:5000/api/comments',
        { postId, content: commentText },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setComments((prev) => ({
        ...prev,
        [postId]: [...(prev[postId] || []), response.data],
      }));
    } catch (error) {
      console.error('Error adding comment:', error.message);
    }
  };

  // Handle image navigation
  const handleImageNav = (postId, category, direction) => {
    setCurrentImageIndex((prev) => {
      const totalImages = posts[category]?.find((post) => post.id === postId).files.length;
      const newIndex = ((prev[postId] || 0) + direction + totalImages) % totalImages;
      return { ...prev, [postId]: newIndex };
    });
  };

  // Handle file/image upload
  const handleProfileImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) setProfileImage(URL.createObjectURL(file));
  };

  // Handle profile changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProfile((prev) => ({ ...prev, [name]: value }));
  };

  const handleProfileSave = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    Object.keys(profile).forEach((key) => formData.append(key, profile[key]));
    if (profileImage) {
      const blob = await fetch(profileImage).then((res) => res.blob());
      formData.append('profileImage', blob, 'profile.jpg');
    }

    const token = localStorage.getItem('token');
    try {
      await axios.post('http://localhost:5000/api/profile', formData, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert('Profile updated successfully!');
    } catch (error) {
      console.error('Error updating profile:', error.message);
    }
  };

  // Initial login state check
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const decoded = jwtDecode(token);
        setLoggedInUser(decoded);
        setIsLoggedIn(true);
      } catch (error) {
        console.error('Error decoding token:', error.message);
      }
    }
  }, []);

  return (
    <div className="app">
      <header className="header">
        <nav className="navbar">
          <ul>
            <li><button onClick={() => handleNavigate('home')}>Home</button></li>
            <li><button onClick={() => handleNavigate('profile')}>Profile</button></li>
            <li><button onClick={() => handleNavigate('community')}>Community</button></li>
          </ul>
        </nav>
      </header>

      {page === 'home' && (
        <div className="home">
          <h1>Welcome to SkillShare Dearborn</h1>
          <img src={logo} alt="SkillShare Logo" />
          <button onClick={() => handleNavigate('login')}>Login</button>
        </div>
      )}

      {page === 'community' && (
        <div className="community">
          <h2>Choose a Category</h2>
          <div className="categories">
            <div onClick={() => setSelectedCategory('Mechanics')}><img src={mechanicsIcon} alt="Mechanics" /></div>
            <div onClick={() => setSelectedCategory('Electronics')}><img src={electronicsIcon} alt="Electronics" /></div>
            <div onClick={() => setSelectedCategory('Appliances')}><img src={appliancesIcon} alt="Appliances" /></div>
          </div>
          {selectedCategory && fetchPosts(selectedCategory)}
        </div>
      )}
    </div>
  );
}

export default App;
