import React, { useState } from 'react';
import './App.css';
import electronicsIcon from './electronics.png';
import appliancesIcon from './appliances.png';
import mechanicsIcon from './mech.png';

function App() {
  const [page, setPage] = useState('home'); // Control the current page
  const [selectedCategory, setSelectedCategory] = useState(null); // Tracks the selected category
  const [isModalOpen, setModalOpen] = useState(false); // Modal visibility
  const [posts, setPosts] = useState({ Mechanics: [], Electronics: [], 'Home Appliances': [] });
  const [newPost, setNewPost] = useState('');

  const handleNavigate = (targetPage) => {
    setPage(targetPage);
  };

  const handleCategoryClick = (category) => {
    setSelectedCategory(category);
    setPage('category'); // Navigate to the specific category
  };

  const handleModalToggle = () => {
    setModalOpen(!isModalOpen);
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    if (newPost.trim() !== '') {
      setPosts({
        ...posts,
        [selectedCategory]: [
          ...posts[selectedCategory],
          { text: newPost, image: getCategoryIcon(selectedCategory) },
        ],
      });
      setNewPost('');
      setModalOpen(false);
    }
  };

  const handleNewPostChange = (e) => {
    setNewPost(e.target.value);
  };

  const getCategoryIcon = (category) => {
    if (category === 'Mechanics') return mechanicsIcon;
    if (category === 'Electronics') return electronicsIcon;
    if (category === 'Home Appliances') return appliancesIcon;
  };

  return (
    <div className="app">
      <header className="header">
        <nav className="navbar">
          <ul>
            <li><button className="nav-link" onClick={() => handleNavigate('home')}>Home</button></li>
            <li><button className="nav-link" onClick={() => handleNavigate('about')}>About</button></li>
            <li><button className="nav-link" onClick={() => handleNavigate('community')}>Community</button></li>
            <li><button className="nav-link" onClick={() => handleNavigate('signup')}>Sign-Up</button></li>
            <li><button className="nav-link" onClick={() => handleNavigate('login')}>Log In</button></li>
          </ul>
        </nav>
      </header>

      {page === 'home' && (
        <main className="main-content">
          <h1>Welcome to SkillShare Dearborn!</h1>
          <p>Your platform for community repair and skill sharing.</p>
        </main>
      )}

      {page === 'about' && (
        <main className="about-content">
          <h1>About Us</h1>
          <p>SkillShare Dearborn is a platform that connects community members to collaborate on repairs and skill-sharing projects.</p>
        </main>
      )}

      {page === 'community' && (
        <main className="community-content">
          <h1>Select a Community Repair Forum</h1>
          <div className="community-categories">
            <div className="community-item" onClick={() => handleCategoryClick('Mechanics')}>
              <img src={mechanicsIcon} alt="Mechanics" />
              <p>Mechanics</p>
            </div>
            <div className="community-item" onClick={() => handleCategoryClick('Electronics')}>
              <img src={electronicsIcon} alt="Electronics" />
              <p>Electronics</p>
            </div>
            <div className="community-item" onClick={() => handleCategoryClick('Home Appliances')}>
              <img src={appliancesIcon} alt="Home Appliances" />
              <p>Home Appliances</p>
            </div>
          </div>
        </main>
      )}

      {page === 'category' && selectedCategory && (
        <main className="category-content">
          <div className="category-header">
            <img src={getCategoryIcon(selectedCategory)} alt={selectedCategory} className="category-image" />
            <h2>{selectedCategory} Forum</h2>
          </div>

          <div className="posts-container">
            {posts[selectedCategory].length === 0 ? (
              <p className="no-posts">No Posts Yet</p>
            ) : (
              posts[selectedCategory].map((post, index) => (
                <div key={index} className="post">
                  <div className="post-text">
                    <p>{post.text}</p>
                  </div>
                  <div className="post-image">
                    <img src={post.image} alt="Post related" />
                  </div>
                </div>
              ))
            )}
          </div>

          <button className="add-button" onClick={handleModalToggle}>+</button>

          {isModalOpen && (
            <div className="modal">
              <div className="modal-content">
                <h2>Create a Post in {selectedCategory}</h2>
                <form onSubmit={handleFormSubmit}>
                  <textarea
                    placeholder={`Write your post for the ${selectedCategory} community...`}
                    className="form-textarea"
                    value={newPost}
                    onChange={handleNewPostChange}
                    required
                  ></textarea>
                  <button type="submit" className="popup-submit-button">Submit</button>
                  <button type="button" className="popup-close-button" onClick={handleModalToggle}>Close</button>
                </form>
              </div>
            </div>
          )}
        </main>
      )}
    </div>
  );
}

export default App;
