import React, { useState } from 'react';
import './App.css';
import logo from './logo.png'; // Adjust paths as needed
import electronicsIcon from './electronics.png';
import appliancesIcon from './appliances.png';
import mechanicsIcon from './mech.png';

function App() {
  const [page, setPage] = useState('home'); // Control which page is shown
  const [posts, setPosts] = useState([]); // Example post data for current category

  const handleNavigate = (targetPage) => {
    setPage(targetPage);

    // Reset posts for each category (mock example, replace with actual logic)
    if (targetPage === 'electronics') {
      setPosts([]); // Empty array means no posts
    } else if (targetPage === 'mechanics') {
      setPosts([
        { content: 'Changed spark plugs in my car.', date: '2024-12-13', time: '10:45 AM' },
        { content: 'Helped a neighbor fix their bike.', date: '2024-12-12', time: '3:15 PM' },
      ]);
    } else if (targetPage === 'appliances') {
      setPosts([
        { content: 'Repaired a toaster.', date: '2024-12-10', time: '8:30 AM' },
      ]);
    }
  };

  const renderPosts = () => {
    if (posts.length === 0) {
      return <p className="no-posts">No Posts Yet</p>;
    }
    return posts.map((post, index) => (
      <div key={index} className="post">
        <p>{post.content}</p>
        <span>{`${post.date} at ${post.time}`}</span>
      </div>
    ));
  };

  return (
    <div className="app">
      <header className="header">
        <nav className="navbar">
          <ul>
            <li><button className="nav-link" onClick={() => handleNavigate('home')}>Home</button></li>
            <li><button className="nav-link" onClick={() => handleNavigate('electronics')}>Electronics</button></li>
            <li><button className="nav-link" onClick={() => handleNavigate('mechanics')}>Mechanics</button></li>
            <li><button className="nav-link" onClick={() => handleNavigate('appliances')}>Home Appliances</button></li>
          </ul>
        </nav>
      </header>

      {page === 'home' && (
        <main className="main-content">
          <div className="logo">
            <img src={logo} alt="SkillShare Dearborn Logo" />
          </div>
          <h1>Welcome to SkillShare - Dearborn!</h1>
          <p>Register as a Resident or Log In Below:</p>
        </main>
      )}

      {(page === 'electronics' || page === 'mechanics' || page === 'appliances') && (
        <main className="forum-content">
          <div className="forum-banner">
            <img
              src={
                page === 'electronics'
                  ? electronicsIcon
                  : page === 'mechanics'
                  ? mechanicsIcon
                  : appliancesIcon
              }
              alt={`${page.charAt(0).toUpperCase() + page.slice(1)} Icon`}
            />
            <p>{page.charAt(0).toUpperCase() + page.slice(1)}</p>
          </div>
          <div className="forum-posts">
            {renderPosts()}
          </div>
        </main>
      )}
    </div>
  );
}

export default App;
