import React, { useState } from 'react';
import './App.css';
import logo from './logo.png'; // Adjust the path to your logo
import electronicsIcon from './electronics.png';
import appliancesIcon from './appliances.png';
import mechanicsIcon from './mech.png';

function App() {
  const [page, setPage] = useState('home'); // Controls which page is shown
  const [selectedCategory, setSelectedCategory] = useState(null); // Tracks selected category
  const [isModalOpen, setModalOpen] = useState(false); // Controls modal visibility
  const [posts, setPosts] = useState({}); // Stores posts for each category
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Tracks login status
  const [currentImageIndex, setCurrentImageIndex] = useState(0); // Controls image navigation

  const handleNavigate = (targetPage) => {
    setPage(targetPage);
  };

  const handleCategoryClick = (category) => {
    setSelectedCategory(category);
    setPage('category'); // Navigate to the selected category's page
  };

  const handleModalToggle = () => {
    setModalOpen(!isModalOpen);
  };

  const handleFormSubmit = (e) => {
    e.preventDefault(); // Prevent default form behavior
    const postContent = e.target.elements['postContent'].value;
    const files = Array.from(e.target.elements['fileUpload'].files);

    if (!postContent.trim()) {
      alert('Post content cannot be empty!');
      return;
    }

    const newPost = {
      content: postContent,
      files: files.map((file) => URL.createObjectURL(file)),
      username: isLoggedIn ? 'John Doe' : 'Anonymous', // Replace with actual username
      date: new Date().toLocaleDateString(),
    };

    setPosts((prevPosts) => ({
      ...prevPosts,
      [selectedCategory]: [...(prevPosts[selectedCategory] || []), newPost],
    }));

    setModalOpen(false); // Close modal
    e.target.reset(); // Reset the form
  };

  const handleNextImage = (images) => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === images.length - 1 ? 0 : prevIndex + 1
    );
  };

  const handlePreviousImage = (images) => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === 0 ? images.length - 1 : prevIndex - 1
    );
  };

  return (
    <div className="app">
      <header className="header">
        <nav className="navbar">
          <ul>
            <li><button className="nav-link" onClick={() => handleNavigate('home')}>Home</button></li>
            <li><button className="nav-link" onClick={() => handleNavigate('about')}>About</button></li>
            <li><button className="nav-link" onClick={() => handleNavigate('community')}>Community</button></li>
            <li><button className="nav-link" onClick={() => handleNavigate('signup')}>Sign-Up</button></li>
            <li><button className="nav-link" onClick={() => handleNavigate('login')}>Log In</button></li>
          </ul>
        </nav>
      </header>

      {/* Home Page */}
      {page === 'home' && (
        <main className="main-content">
          <div className="logo">
            <img src={logo} alt="SkillShare Dearborn Logo" />
          </div>
          <h1>Welcome to SkillShare - Dearborn!</h1>
          <p>Register as a Resident or Log In Below:</p>
          <div className="buttons">
            <button className="button" onClick={() => handleNavigate('signup')}>Register</button>
            <button className="button" onClick={() => handleNavigate('login')}>Log In</button>
          </div>
        </main>
      )}

      {/* About Page */}
      {page === 'about' && (
        <main className="about-content">
          <div className="about-banner">
            <img src={logo} alt="SkillShare Dearborn Logo" className="about-logo" />
            <h1>About Us</h1>
          </div>
          <section className="about-section">
            <p>
              Welcome to SkillShare Dearborn, a community-driven platform dedicated to fostering sustainability, skill-sharing,
              and environmental consciousness through collaborative repair events.
            </p>
            <p>
              Join us to learn new skills, save money, and contribute to a more environmentally friendly Dearborn!
            </p>
          </section>
        </main>
      )}

      {/* Community Page */}
      {page === 'community' && (
        <main className="community-content">
          <h1>Find a Community Repair Forum</h1>
          <div className="community-categories">
            <div className="community-item" onClick={() => handleCategoryClick('Mechanics')}>
              <img src={mechanicsIcon} alt="Mechanics" />
              <p>Mechanics</p>
            </div>
            <div className="community-item" onClick={() => handleCategoryClick('Electronics')}>
              <img src={electronicsIcon} alt="Electronics" />
              <p>Electronics</p>
            </div>
            <div className="community-item" onClick={() => handleCategoryClick('Home Appliances')}>
              <img src={appliancesIcon} alt="Home Appliances" />
              <p>Home Appliances</p>
            </div>
          </div>
        </main>
      )}

      {/* Category Page */}
      {page === 'category' && selectedCategory && (
        <main className="category-content">
          <div className="community-logo">
            <img
              src={
                selectedCategory === 'Mechanics'
                  ? mechanicsIcon
                  : selectedCategory === 'Electronics'
                  ? electronicsIcon
                  : appliancesIcon
              }
              alt={selectedCategory}
            />
            <h2>{selectedCategory} Forum</h2>
          </div>

          {/* Posts Section */}
          <div className="posts">
            {posts[selectedCategory]?.length > 0 ? (
              posts[selectedCategory].map((post, index) => (
                <div key={index} className="post">
                  <div className="image-slider">
                    {post.files.length > 1 && (
                      <button
                        className="arrow left-arrow"
                        onClick={() => handlePreviousImage(post.files)}
                      >
                        &lt;
                      </button>
                    )}
                    <img
                      src={post.files[currentImageIndex]}
                      alt="Post content"
                      className="post-image"
                    />
                    {post.files.length > 1 && (
                      <button
                        className="arrow right-arrow"
                        onClick={() => handleNextImage(post.files)}
                      >
                        &gt;
                      </button>
                    )}
                  </div>
                  <div className="post-content">
                    <p className="post-text">{post.content}</p>
                    <div className="post-footer">
                      <span className="post-username">By {post.username}</span>
                      <span className="post-date">{post.date}</span>
                    </div>
                  </div>
                </div>
              ))
            ) : (
              <div className="no-posts">
                <p>No Posts Yet</p>
              </div>
            )}
          </div>

          {/* Add Button */}
          <button className="add-button" onClick={handleModalToggle}>+</button>

          {/* Pop-up Modal */}
          {isModalOpen && (
            <div className="modal">
              <div className="modal-content">
                <h2>Create a Post in {selectedCategory}</h2>
                <form onSubmit={handleFormSubmit}>
                  <textarea
                    name="postContent"
                    placeholder={`Connect with the ${selectedCategory} community here...`}
                    className="form-textarea"
                    required
                  ></textarea>
                  <div className="form-actions">
                    <input type="file" name="fileUpload" className="form-file" multiple />
                    <button type="submit" className="popup-submit-button">Submit</button>
                  </div>
                </form>
                <button className="popup-close-button" onClick={handleModalToggle}>Close</button>
              </div>
            </div>
          )}
        </main>
      )}

      {/* Sign-Up Page */}
      {page === 'signup' && (
        <main className="main-content signup-form">
          <div className="logo">
            <img src={logo} alt="SkillShare Dearborn Logo" />
          </div>
          <h1>Sign Up Here!</h1>
          <form onSubmit={(e) => { e.preventDefault(); setPage('community'); }}>
            <input type="text" placeholder="Full Name" className="form-input" required />
            <input type="email" placeholder="Email Address" className="form-input" required />
            <input type="password" placeholder="Password" className="form-input" required />
            <input type="text" placeholder="Zipcode" className="form-input" required />
            <button type="submit" className="button">Submit</button>
          </form>
          <button className="button back-button" onClick={() => handleNavigate('home')}>Back</button>
        </main>
      )}

      {/* Login Page */}
      {page === 'login' && (
        <main className="main-content login-form">
          <div className="logo">
            <img src={logo} alt="SkillShare Dearborn Logo" />
          </div>
          <h1>Log In</h1>
          <form onSubmit={(e) => { e.preventDefault(); setPage('community'); }}>
            <input type="email" placeholder="Email Address" className="form-input" required />
            <input type="password" placeholder="Password" className="form-input" required />
            <button type="submit" className="button">Log In</button>
          </form>
          <button className="button back-button" onClick={() => handleNavigate('home')}>Back</button>
        </main>
      )}
    </div>
  );
}

export default App;
