import React, { useState } from 'react';
import './App.css';
import logo from './logo.png'; // Adjust paths as needed
import electronicsIcon from './electronics.png';
import appliancesIcon from './appliances.png';
import mechanicsIcon from './mech.png';

function App() {
  const [page, setPage] = useState('home'); // Tracks which page is active
  const [posts, setPosts] = useState([]); // Example post data for the current category
  const [showPostForm, setShowPostForm] = useState(false); // Tracks whether the Add Post form is visible

  const handleNavigate = (targetPage) => {
    setPage(targetPage);

    // Reset posts for each category (mock example, replace with actual logic)
    if (targetPage === 'electronics') {
      setPosts([]); // Empty array means no posts
    } else if (targetPage === 'mechanics') {
      setPosts([]); // Empty array for now
    } else if (targetPage === 'appliances') {
      setPosts([]); // Empty array for now
    }
    setShowPostForm(false); // Hide the Add Post form when switching categories
  };

  const handleAddPostClick = () => {
    setShowPostForm(true); // Show the Add Post form
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    const newPost = {
      content: e.target.postContent.value,
      date: new Date().toLocaleDateString(),
      time: new Date().toLocaleTimeString(),
    };
    setPosts([...posts, newPost]); // Add the new post to the posts list
    setShowPostForm(false); // Hide the Add Post form
  };

  return (
    <div className="app">
      <header className="header">
        <nav className="navbar">
          <ul>
            <li><button className="nav-link" onClick={() => handleNavigate('home')}>Home</button></li>
            <li><button className="nav-link" onClick={() => handleNavigate('about')}>About</button></li>
            <li><button className="nav-link" onClick={() => handleNavigate('community')}>Community</button></li>
            <li><button className="nav-link" onClick={() => handleNavigate('signup')}>Sign-Up</button></li>
            <li><button className="nav-link" onClick={() => handleNavigate('login')}>Log In</button></li>
          </ul>
        </nav>
      </header>

      {page === 'community' && (
        <main className="community-content">
          <h1>Find a Community Repair Forum</h1>
          <div className="community-categories">
            <div className="community-item" onClick={() => handleNavigate('electronics')}>
              <img src={electronicsIcon} alt="Electronics" />
              <p>Electronics</p>
            </div>
            <div className="community-item" onClick={() => handleNavigate('mechanics')}>
              <img src={mechanicsIcon} alt="Mechanics" />
              <p>Mechanics</p>
            </div>
            <div className="community-item" onClick={() => handleNavigate('appliances')}>
              <img src={appliancesIcon} alt="Home Appliances" />
              <p>Home Appliances</p>
            </div>
          </div>
        </main>
      )}

      {(page === 'electronics' || page === 'mechanics' || page === 'appliances') && (
        <main className="category-content">
          <div className="forum-banner">
            <img
              src={
                page === 'electronics'
                  ? electronicsIcon
                  : page === 'mechanics'
                  ? mechanicsIcon
                  : appliancesIcon
              }
              alt={`${page.charAt(0).toUpperCase() + page.slice(1)} Icon`}
            />
            <p>{page.charAt(0).toUpperCase() + page.slice(1)}</p>
          </div>

          {showPostForm ? (
            <form className="add-post-form" onSubmit={handleFormSubmit}>
              <textarea
                name="postContent"
                placeholder="Connect with your community here..."
                required
              />
              <button type="submit" className="button">Submit</button>
            </form>
          ) : (
            <>
              {posts.length === 0 ? (
                <div className="no-posts-container">
                  <p className="no-posts">No Posts Yet</p>
                  <button className="add-post-button" onClick={handleAddPostClick}>
                    +
                  </button>
                </div>
              ) : (
                <div className="posts-list">
                  {posts.map((post, index) => (
                    <div key={index} className="post">
                      <p>{post.content}</p>
                      <span>{`${post.date} at ${post.time}`}</span>
                    </div>
                  ))}
                </div>
              )}
            </>
          )}
        </main>
      )}
    </div>
  );
}

export default App;
