        headers: { Authorization: 'Bearer ${token}' },
      });

      setComments((prevComments) => ({
        ...prevComments,
        [postId]: response.data,
      }));
    } catch (error) {
      console.error('Error fetching comments:', error.message);
      alert('Failed to fetch comments.');
    }
  };

  const handleDeletePost = async (postId, category) => {
    const confirmDelete = window.confirm("Are you sure you want to delete this post?");
    if (!confirmDelete) return;

    const token = localStorage.getItem('token');
    try {
        await axios.delete('http://localhost:5000/api/posts/${postId}', {
            headers: { Authorization: 'Bearer ${token}' },
        });

        // Update posts in the frontend
        setPosts((prevPosts) => ({
            ...prevPosts,
            [category]: prevPosts[category].filter((post) => post.id !== postId),
        }));

        alert('Post deleted successfully.');
    } catch (error) {
        console.error('Error deleting post:', error);
        alert('Failed to delete post.');
    }
};


const handleSignUp = async (e) => {
  e.preventDefault();
  const formData = new FormData(e.target);
  const selectedSkills = Array.from(formData.getAll('skills'));

  // Fill skill1, skill2, skill3, and assign 'None' if fewer than three skills are selected
  const [skill1, skill2, skill3] = [
    selectedSkills[0] || 'None',
    selectedSkills[1] || 'None',
    selectedSkills[2] || 'None',
  ];

  const userData = {
    name: formData.get('name'),
    email: formData.get('email'),
    password: formData.get('password'),
    zipcode: formData.get('zipcode'),
    skill1,
    skill2,
    skill3,
  };

  try {
    await axios.post('http://localhost:5000/api/users', userData);
    alert('User registered successfully!');
    setPage('community');
  } catch (error) {
    console.error('Error during sign-up:', error);
    alert('Failed to register user.');
  }
};

